$(document).ready(function() {

//OWL carousel
    var owl = $(".slider");
    owl.owlCarousel({
        stagePadding: 0,
        loop:true,
        items:1,
        itemClass: "slide-wrap",
        nav: true,
        navText: "",
        dots: false,
        autoplay: [1, 2]
    });

    $(".toggle-mnu").click(function() {
        $(this).toggleClass("on");
        $("menu").toggleClass("menu");
        return false;
    });

});
$(document).on("scroll",function(){
    if($(document).scrollTop()>100){
        $("header").addClass("small");
    } else{
        $("header").removeClass("small");
    }
});
